import React, { useEffect, useState } from 'react';

function Statistics() {
    const [stats, setStats] = useState(null);

    const fetchStats = () => {
        fetch('http://localhost/api/stats')
            .then(response => response.json())
            .then(data => setStats(data))
            .catch(error => console.error('Error fetching statistics:', error));
    };

    useEffect(fetchStats, []);

    if (!stats) return <p>Loading...</p>;

    return (
        <div className="stats-section">
            <h2>Statistics</h2>
            <p>Number of Contacts: {stats.numContacts}</p>
            <p>Number of Phones: {stats.numPhones}</p>
            <p>Newest Contact Timestamp: {new Date(stats.newestContact).toLocaleString()}</p>
            <p>Oldest Contact Timestamp: {new Date(stats.oldestContact).toLocaleString()}</p>
            <button onClick={fetchStats}>Refresh</button>
        </div>
    );
}

export default Statistics;
